import { CREATE, UPDATE, DELETE, FETCH_ALL, FETCH_BY_SEARCH, FETCH_POST } from "../constants/actionTypes.js";

export default (state = [], actions) => {
    switch (actions.type){
        case FETCH_ALL:
            return {
                ...state,
                posts: actions.payload.data,
                currentPage: actions.payload.currentPage,
                numberOfPages: actions.payload.numberOfPages,
                };
        case FETCH_BY_SEARCH:
            return { ...state, posts: actions.payload };
        case FETCH_POST:
            return { ...state, post: actions.payload.post };
        case CREATE:
            return [...state, actions.payload];
        case UPDATE:
            return state.map((post) => (post._id === actions.payload._id ? actions.payload : post ));
        case DELETE:
            return state.filter((post)=> post._id !== actions.payload);
        default:
            return state;
    }
}
