import { CREATE, UPDATE, DELETE, FETCH_ALL, FETCH_BY_SEARCH, FETCH_POST } from "../constants/actionTypes.js";

export default (state = { posts: [] }, actions) => {
    switch (actions.type){
        case FETCH_ALL:
            return {...state, posts: actions.payload};
        case FETCH_BY_SEARCH:
            return actions.payload;
        case FETCH_POST:
            return  actions.payload.post;
        case CREATE:
            return [...posts, actions.payload];
        case UPDATE:
            return posts.map((post) => (post._id === actions.payload._id ? actions.payload : post ));
        case DELETE:
            return posts.filter((post)=> post._id !== actions.payload);
        default:
            return posts;
    }
}
