import axios from 'axios';

const url = 'http://localhost:5000/pegawai';

export const fetchPostsp = () => axios.get(url);
export const createPostp = (newPostp) => axios.post(url, newPostp);
export const updatePostp = (id, updatePostp) => axios.patch(`${url}/${id}`, updatePostp);
export const deletePostp = (id) => axios.delete(`${url}/${id}`);
