import React, { useEffect, useState } from 'react';
import { Paper, Typography, CircularProgress, Divider, CardActions } from '@material-ui/core/';
import { useDispatch, useSelector } from 'react-redux';
import moment from 'moment';
import { useParams, useHistory } from 'react-router-dom';

import { getPost, getPostsBySearch } from '../../actions/posts';
import useStyles from './styles';

const PelangganDetails = () => {

  const { isLoading } = useSelector((state) => state.posts);
  const [post, setPost] = useState({});
  const dispatch = useDispatch();
  const history = useHistory();
  const classes = useStyles();
  const { id } = useParams();

  useEffect(() => {
    dispatch(getPost(id));
  }, [id]);


  if (!post) return null;

  if (isLoading) {
    return (
      <Paper elevation={6} className={classes.loadingPaper}>
        <CircularProgress size="7em" />
      </Paper>
    );
  }

  return (
    <Paper style={{ padding: '20px', borderRadius: '15px' }} elevation={6}>
      <div className={classes.card}>
        <div className={classes.section}>
          <Typography variant="h3" component="h2">{post.namaPelanggan}</Typography>
          <Typography gutterBottom variant="subtitle1" color="inherit" component="h2">{post.noPelanggan}</Typography>
          <Divider style={{ margin: '20px 0' }} />
          <CardActions className={classes.cardActions} >
            <Typography variant="subtitle1" color="inherit">Slot:  {post.noSlot}</Typography>
            <Divider orientation="vertical" flexItem />
            <Typography variant="subtitle1" color="inherit">{post.statusKendaraan}</Typography>
          </CardActions>
          <Divider style={{ margin: '20px 0' }} />
          <Typography variant="body1">{moment(post.createdAt).fromNow()}</Typography>
          <Divider style={{ margin: '20px 0' }} />
          <CardActions className={classes.cardActions} >
            <Typography variant="subtitle1" color="inherit">{post.merkMotor}</Typography>
            <Divider orientation="vertical" flexItem />
            <Typography variant="subtitle1" color="inherit">{post.plat}</Typography>
          </CardActions>
          <Divider style={{ margin: '20px 0' }} />
          <CardActions className={classes.cardActions} >
            <Typography variant="h6" color="inherit">Pencuci {post.namaPencuci}</Typography>
            <Divider orientation="vertical" flexItem />
            <Typography variant="subtitle1" color="inherit">Rp.10.000</Typography>
          </CardActions>
          <Divider style={{ margin: '20px 0' }} />
          <Typography variant="h6">Created by: {post.name}</Typography>
          <Divider style={{ margin: '20px 0' }} />
        </div>
        <div className={classes.imageSection}>
          <img className={classes.media} src={post.selectedFile} alt={post.title} />
        </div>
      </div>
    </Paper>
  )
}

export default PelangganDetails;
