import React, { useEffect } from 'react';
import { Paper, Typography, CircularProgress, Divider, CardActions } from '@material-ui/core/';
import { useDispatch, useSelector } from 'react-redux';
import moment from 'moment';
import { useParams, useHistory } from 'react-router-dom';
import Axios from 'axios';


import { getPost, getPostsBySearch } from '../../actions/posts';
import useStyles from './styles';

const PelangganDetails = (props) => {
  const user = JSON.parse(localStorage.getItem('profile'));
  const history = useHistory();
  const [data, setData] = useState({})

    useEffect(() => {
        console.log('params: ', props.match.params.id)
        const id = props.match.params.id
        Axios.get(`http://192.168.1.3:5000/pelanggan/${id}`)
        .then(res => {
            console.log('success: ', res);
            setData(res.data.data)
            console.log(data);
        })
        .catch(err => {
            console.log('err: ', err);
        })
    }, [])


  return (
    <Paper style={{ padding: '20px', borderRadius: '15px' }} elevation={6}>
      <div className={classes.card}>
        <div className={classes.section}>
          <Typography variant="h3" component="h2">{data.namaPelanggan}</Typography>
          <Typography gutterBottom variant="subtitle1" color="inherit" component="h2">{data.noPelanggan}</Typography>
          <Divider style={{ margin: '20px 0' }} />
          <CardActions className={classes.cardActions} >
            <Typography variant="subtitle1" color="inherit">Slot:  {data.noSlot}</Typography>
            <Divider orientation="vertical" flexItem />
            <Typography variant="subtitle1" color="inherit">{data.statusKendaraan}</Typography>
          </CardActions>
          <Divider style={{ margin: '20px 0' }} />
          <Typography variant="body1">{moment(data.createdAt).fromNow()}</Typography>
          <Divider style={{ margin: '20px 0' }} />
          <CardActions className={classes.cardActions} >
            <Typography variant="subtitle1" color="inherit">{data.merkMotor}</Typography>
            <Divider orientation="vertical" flexItem />
            {(user?.result?._id === post?.creator || user?.result?._id === "62729e1306510726d49b02a2") && (
              <Typography variant="subtitle1" color="inherit">{data.plat}</Typography>
            )}
          </CardActions>
          <Divider style={{ margin: '20px 0' }} />
          <CardActions className={classes.cardActions} >
            <Typography variant="h6" color="inherit">Pencuci {data.namaPencuci}</Typography>
            <Divider orientation="vertical" flexItem />
            <Typography variant="subtitle1" color="inherit">Rp.10.000</Typography>
          </CardActions>
          <Divider style={{ margin: '20px 0' }} />
          <Typography variant="h6">Created by: {data.name}</Typography>
          <Divider style={{ margin: '20px 0' }} />
        </div>
        <div className={classes.imageSection}>
          <img className={classes.media} src={data.selectedFile} alt={data.namaPelanggan} />
        </div>
      </div>
    </Paper>
  )
}

export default PelangganDetails;
