import React, { useState, useEffect } from 'react';
import { Paper, Typography, CircularProgress, Divider, CardActions } from '@material-ui/core/';
import { useDispatch, useSelector } from 'react-redux';
import moment from 'moment';
import { useParams, useHistory } from 'react-router-dom';

//import { getPost, getPostsBySearch } from '../../actions/posts';
import useStyles from './styles';
import axios from 'axios';

const pelangganState = { namaPelanggan: '', noPelanggan: '', noSlot: '', statusKendaraan: '', createdAt: '', merkMotor: '', plat: '', namaPencuci: '', name: '', selectedFile: '' };

const PelangganDetails = () => {

  const [formData, setFormData] = useState('pelangganState');

  const history = useHistory();
  const classes = useStyles();
  const { id } = useParams();

  const getPelangganById = async () => {
    const response = await axios.get(`http://192.168.1.3:5000/pelanggan/${id}`);
    setFormData(response.data.formData);
  }

  useEffect(() => {
    getPelangganById();
  }, []);



  return (
    <Paper style={{ padding: '20px', borderRadius: '15px' }} elevation={6}>
      <div className={classes.card}>
        <div className={classes.section}>
          <Typography variant="h3" component="h2">{formData.namaPelanggan}</Typography>
          <Typography gutterBottom variant="subtitle1" color="inherit" component="h2">{formData.noPelanggan}</Typography>
          <Divider style={{ margin: '20px 0' }} />
          <CardActions className={classes.cardActions} >
            <Typography variant="subtitle1" color="inherit">Slot:  {formData.noSlot}</Typography>
            <Divider orientation="vertical" flexItem />
            <Typography variant="subtitle1" color="inherit">{formData.statusKendaraan}</Typography>
          </CardActions>
          <Divider style={{ margin: '20px 0' }} />
          <Typography variant="body1">{moment(formData.createdAt).fromNow()}</Typography>
          <Divider style={{ margin: '20px 0' }} />
          <CardActions className={classes.cardActions} >
            <Typography variant="subtitle1" color="inherit">{formData.merkMotor}</Typography>
            <Divider orientation="vertical" flexItem />
            <Typography variant="subtitle1" color="inherit">{formData.plat}</Typography>
          </CardActions>
          <Divider style={{ margin: '20px 0' }} />
          <CardActions className={classes.cardActions} >
            <Typography variant="h6" color="inherit">Pencuci {formData.namaPencuci}</Typography>
            <Divider orientation="vertical" flexItem />
            <Typography variant="subtitle1" color="inherit">Rp.10.000</Typography>
          </CardActions>
          <Divider style={{ margin: '20px 0' }} />
          <Typography variant="h6">Created by: {formData.name}</Typography>
          <Divider style={{ margin: '20px 0' }} />
        </div>
        <div className={classes.imageSection}>
          <img className={classes.media} src={formData.selectedFile} alt={formData.namaPelanggan} />
        </div>
      </div>
    </Paper>
  )
}

export default PelangganDetails;
