import { CREATE, UPDATE, DELETE, FETCH_ALL, FETCH_BY_SEARCH, FETCH_POST } from "../constants/actionTypes.js";

export default (state = { posts: [] }, actions) => {
    switch (actions.type){
        case FETCH_ALL:
            return { ...state, posts: actions.payload };
        case FETCH_BY_SEARCH:
            return { ...state, posts: actions.payload.data };
        case FETCH_POST:
            return  { ...state, post: actions.payload.post };
        case CREATE:
            return { ...state, posts: [...state.posts, actions.payload] };
        case UPDATE:
            return { ...state, posts: state.posts.map((post) => (post._id === actions.payload._id ? actions.payload : post)) };
        case DELETE:
            return { ...state, posts: state.posts.filter((post) => post._id !== actions.payload) };
        default:
            return state;
    }
}
