import axios from 'axios';

const API = axios.create({ baseURL: 'http://localhost:5000' });

//const url = 'http://localhost:5000/pegawai';

export const fetchPostsp = () => axios.get('/pegawai');
export const createPostp = (newPostp) => axios.post('/pegawai', newPostp);
export const updatePostp = (id, updatePostp) => axios.patch(`/pegawai/${id}`, updatePostp);
export const deletePostp = (id) => axios.delete(`/pegawai/${id}`);

export const signIn = (formData) => API.post('/users/signin', formData);
export const signUp = (formData) => API.post('/users/signup', formData);
