import React, { useEffect } from 'react';
import { Paper, Typography, CircularProgress, Divider, CardActions } from '@material-ui/core/';
import { useDispatch, useSelector } from 'react-redux';
import moment from 'moment';
import { useParams, useHistory } from 'react-router-dom';

import useStyles from '../PelangganDetails/styles';
import Boy from '../../images/boy.png';

import { getPost, getPostsBySearch } from '../../actions/posts';

const PostpDetails = () => {
  const user = JSON.parse(localStorage.getItem('profile'));

  const { post, posts, isLoading } = useSelector((state) => state.posts);
  const dispatch = useDispatch();
  const history = useHistory();
  const classes = useStyles();
  const { id } = useParams();

  useEffect(() => {
    dispatch(getPost(id));
    console.log(id, "ini id");
  }, [id]);

  useEffect(() => {
    if (post) {
      dispatch(getPostsBySearch({ search: 'none' }));
    }
  }, [post]);

  if (!post) return null;

  const openPost = (_id) => history.push(`/posts/${_id}`);

  if (isLoading) {
    return (
      <Paper elevation={6} className={classes.loadingPaper}>
        <CircularProgress size="7em" />
      </Paper>
    );
  }

  return (
    <Paper style={{ padding: '20px', borderRadius: '15px' }} elevation={6}>
      <div className={classes.card}>
        <div className={classes.section}>
          <Typography variant="h3" component="h2">Saprol</Typography>
          <Typography gutterBottom variant="subtitle1" color="inherit" component="h2">081325468899</Typography>
          <Divider style={{ margin: '20px 0' }} />
          <Typography variant="h6" color="inherit">Usia : 29 Tahun </Typography>
          <Divider style={{ margin: '20px 0' }} />
          <Typography variant="h6" color="inherit">Lama Kerja : 2 months ago </Typography>
          <Divider style={{ margin: '20px 0' }} />
          <Typography variant="h6">Created by: admin 1</Typography>
          <Divider style={{ margin: '20px 0' }} />
        </div>
        <div className={classes.imageSection}>
          <img className={classes.media} src={Boy} alt="Wandi" />
        </div>
      </div>
    </Paper>
  )
}

export default PostpDetails;
